interface StatusDistributionChartProps {
  statusCounts: Record<string, number>;
}
const STATUS_COLORS = {
  PENDING: "#FFCE56",
  REJECTED: "#FF6384",
  PROCESSED: "#36A2EB",
};
const StatusDistributionChart: React.FC<StatusDistributionChartProps> = ({
  statusCounts,
}) => {
  const data = Object.entries(statusCounts).map(([status, count]) => ({
    name: status,
    value: count,
  }));
  const chartConfig: ChartConfig = {
    PENDING: { label: "Pending", color: STATUS_COLORS.PENDING },
    REJECTED: { label: "Rejected", color: STATUS_COLORS.REJECTED },
    PROCESSED: { label: "Processed", color: STATUS_COLORS.PROCESSED },
  };
  const renderCustomizedLabel = ({
    cx,
    cy,
    midAngle,
    innerRadius,
    outerRadius,
    percent,
  }: any) => {
    const RADIAN = Math.PI / 180;
    const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
    const x = cx + radius * Math.cos(-midAngle * RADIAN);
    const y = cy + radius * Math.sin(-midAngle * RADIAN);
    return (
      <text
        x={x}
        y={y}
        fill="white"
        textAnchor={x > cx ? "start" : "end"}
        dominantBaseline="central"
      >
        {`${(percent * 100).toFixed(0)}%`}
      </text>
    );
  };
  return (
    <Card>
      <CardHeader>
        <CardTitle>Status Distribution</CardTitle>
      </CardHeader>
      <CardContent>
        <ChartContainer config={chartConfig} className="min-h-[300px] w-full">
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={data}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={renderCustomizedLabel}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {data.map((entry, index) => (
                  <Cell
                    key={`cell-${index}`}
                    fill={
                      STATUS_COLORS[entry.name as keyof typeof STATUS_COLORS]
                    }
                  />
                ))}
              </Pie>
              <Tooltip
                formatter={(value: number, name: string) => {
                  const total = data.reduce((sum, item) => sum + item.value, 0);
                  const percent = ((value / total) * 100).toFixed(1);
                  return [`${value} (${percent}%)`, name];
                }}
              />
              <Legend layout="vertical" align="right" verticalAlign="middle" />
            </PieChart>
          </ResponsiveContainer>
        </ChartContainer>
      </CardContent>
    </Card>
  );
};
interface InfoPanelProps {
  totalRequests: number;
  statusCounts: Record<string, number>;
  totalAmount: number;
  pendingAmount: number;
}
function InfoPanel({
