interface DataTableRowActionsProps<TData> {
  row: Row<TData>;
  tableSchema: z.ZodSchema<UserSchema>;
}
export function UserTableRowActions<TData>({
  row,
  tableSchema,
}: DataTableRowActionsProps<TData>) {
  const user = tableSchema.parse(row.original);
  const [activeDialog, setActiveDialog] = React.useState<string | null>(null);
  const handleAction = async (action: string, status?: string) => {
    try {
      let url = `${window.ENV.BACKEND_URL}/api/v1/admin/${user.email}`;
      let method = "POST";
      let body = null;
      switch (action) {
        case "ban":
          url += "/ban";
          break;
        case "unban":
          url += "/unban";
          break;
        case "delete":
          method = "DELETE";
          break;
        case "reviewApplication":
          url = `${window.ENV.BACKEND_URL}/api/v1/admin/seller-profile/${user.email}`;
          method = "PUT";
          body = JSON.stringify({ verificationStatus: status });
          break;
        default:
          throw new Error("Invalid action");
      }
      const res = await fetch(url, {
        method: method,
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        body: body,
      });
      if (!res.ok) {
        throw new Error("Oh no! Something went wrong!");
      }
      toast.success(`Action ${action} completed successfully`);
      window.location.reload();
    } catch (e) {
      showErrorToast(e);
    }
  };
  const renderDialogContent = () => {
    switch (activeDialog) {
      case "ban":
        return (
          <BanUserDialog
            user={user}
            onClose={() => setActiveDialog(null)}
            onAction={() => handleAction("ban")}
          />
        );
      case "unban":
        return (
          <UnbanUserDialog
            user={user}
            onClose={() => setActiveDialog(null)}
            onAction={() => handleAction("unban")}
          />
        );
      case "delete":
        return (
          <DeleteUserDialog
            user={user}
            onClose={() => setActiveDialog(null)}
            onAction={() => handleAction("delete")}
          />
        );
      case "reviewApplication":
        return (
          <ReviewApplicationDialog
            user={user}
            onClose={() => setActiveDialog(null)}
            onAction={(action, status) => handleAction(action, status)}
          />
        );
      default:
        return null;
    }
  };
  return (
    <AlertDialog
      open={!!activeDialog}
      onOpenChange={() => setActiveDialog(null)}
    >
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button
            variant="ghost"
            className="flex h-8 w-8 p-0 data-[state=open]:bg-muted"
          >
            <DotsHorizontalIcon className="h-4 w-4" />
            <span className="sr-only">Open menu</span>
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end" className="w-[160px]">
          {!user.bannedUntil ? (
            <Link to={`/app/admin/users/user-profile/${user.email}`}>
              <DropdownMenuItem>Edit Profile</DropdownMenuItem>
            </Link>
          ) : (
            <DropdownMenuItem disabled>Edit Profile</DropdownMenuItem>
          )}
          {user.role === "SELLER" && (
            <>
              <Link to={`/app/admin/users/seller-profile/${user.email}`}>
                <DropdownMenuItem>Edit Seller Profile</DropdownMenuItem>
              </Link>
              <DropdownMenuItem
                onClick={() => setActiveDialog("reviewApplication")}
              >
                Review Application
              </DropdownMenuItem>
            </>
          )}
          {user.bannedUntil ? (
            <DropdownMenuItem onClick={() => setActiveDialog("unban")}>
              Unban User
            </DropdownMenuItem>
          ) : (
            <DropdownMenuItem
              onClick={() => setActiveDialog("ban")}
              className="text-red-500"
            >
              Ban User
            </DropdownMenuItem>
          )}
          <DropdownMenuItem
            onClick={() => setActiveDialog("delete")}
            className="text-red-500"
          >
            Delete User
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>
      <AlertDialogContent className="w-1/2 max-w-full">
        {renderDialogContent()}
      </AlertDialogContent>
    </AlertDialog>
  );
}
