interface DataTableRowActionsProps<TData> {
  row: Row<TData>;
  tableSchema: ZodSchema<CommentSchema>;
}
export function CommentTableRowActions<TData>({
  row,
  tableSchema,
}: DataTableRowActionsProps<TData>) {
  const comment = tableSchema.parse(row.original);
  const [activeDialog, setActiveDialog] = React.useState<string | null>(null);
  const handleAction = async (action: string) => {
    try {
      // eslint-disable-next-line
      let url = `${window.ENV.BACKEND_URL}/api/v1/comments/${comment.id}`;
      let method = "PUT";
      switch (action) {
        case "removeFlag":
          url += "/revert";
          method = "PUT";
          break;
        case "delete":
          method = "DELETE";
          break;
        default:
          throw new Error("Invalid action");
      }
      const res = await fetch(url, {
        method: method,
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(action === "removeFlag" ? { flag: "NONE" } : {}),
      });
      if (!res.ok) {
        throw new Error("Oh no! Something went wrong!");
      }
      toast.success(
        `Comment ${action === "removeFlag" ? "updated" : "deleted"} successfully`,
      );
      window.location.reload();
    } catch (e) {
      showErrorToast(e);
    }
  };
  const renderDialogContent = () => {
    switch (activeDialog) {
      case "removeFlag":
        return (
          <RemoveFlagDialog
            comment={comment}
            onClose={() => setActiveDialog(null)}
            onAction={() => handleAction("removeFlag")}
          />
        );
      case "delete":
        return (
          <DeleteCommentDialog
            comment={comment}
            onClose={() => setActiveDialog(null)}
            onAction={() => handleAction("delete")}
          />
        );
      default:
        return null;
    }
  };
  return (
    <AlertDialog
      open={!!activeDialog}
      onOpenChange={() => setActiveDialog(null)}
    >
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button
            variant="ghost"
            className="flex h-8 w-8 p-0 data-[state=open]:bg-muted"
          >
            <DotsHorizontalIcon className="h-4 w-4" />
            <span className="sr-only">Open menu</span>
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end" className="w-[160px]">
          <DropdownMenuItem onClick={() => setActiveDialog("removeFlag")}>
            Remove Flag
          </DropdownMenuItem>
          <DropdownMenuItem
            onClick={() => setActiveDialog("delete")}
            className="text-red-500"
          >
            Delete Comment
          </DropdownMenuItem>
        </DropdownMenuContent>
      </DropdownMenu>
      <AlertDialogContent>{renderDialogContent()}</AlertDialogContent>
    </AlertDialog>
  );
}
