        `${window.ENV.BACKEND_URL}/api/v1/repo/${repo.id}/publish`,
        {
          method: "PUT",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          signal: abortControllerRef.current.signal,
        },
      );
      if (!response.ok) {
        throw new Error("Failed to publish repository");
