    .string()
    .refine(isValidPhoneNumber, { message: "Invalid phone number" })
    .or(z.literal("")),
});
type FormData = z.infer<typeof schema>;
export { schema, type FormData };
export default function ProfileEditComponent() {
  const { user, isLoading, checkLoginStatus } = useUserStore();
  const [userAvatar, setUserAvatar] = useState<string | null>(null);
  const [formKey, setFormKey] = useState(0);
  const [initialLoading, setInitialLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const form = useForm<FormData>({
    resolver: zodResolver(schema),
    defaultValues: {
      name: "",
      phoneNumber: "",
    },
  });
  const generateAvatar = () => {
    const size = 128;
    const avatar = createAvatar(notionists, {
      seed: Math.random().toString(16).substring(2, 18),
      radius: 20,
    }).toDataUriSync();
    if (!window) return;
    const canvas = document.createElement("canvas");
    canvas.width = size;
    canvas.height = size;
    const context = canvas.getContext("2d");
    const img = document.createElement("img");
    img.width = size;
    img.height = size;
    img.setAttribute("src", avatar);
    img.onload = function () {
      context?.drawImage(img, 0, 0, size, size);
      const png = canvas.toDataURL("image/png");
      setUserAvatar(png);
    };
    return avatar;
  };
  useEffect(() => {
    const initializeUser = async () => {
      await checkLoginStatus();
      //setUserAvatar(user?.profile.profileImg ?? '');
      setInitialLoading(false);
    };
    initializeUser();
  }, []);
  useEffect(() => {
    if (user) {
      setUserAvatar(user.profile.profileImg ?? "");
      form.reset({
        name: user.profile.name ?? "",
        phoneNumber: user.profile.phoneNumber ?? "",
      });
      setFormKey((prevKey) => prevKey + 1);
    }
  }, [user, form]);
  const onSubmit = async (data: FormData) => {
    if (!user || isSubmitting) return;
    setIsSubmitting(true);
    try {
      let body;
      const fetchOptions: RequestInit = {
        method: "PUT",
        credentials: "include",
      };
      if (userAvatar && userAvatar !== user.profile.profileImg) {
        const formData = new FormData();
        if (data.name) formData.append("name", data.name);
        if (data.phoneNumber) formData.append("phoneNumber", data.phoneNumber);
        const response = await fetch(userAvatar);
        const blob = await response.blob();
        formData.append("profileImg", blob, "avatar.png");
        body = formData;
      } else {
        body = JSON.stringify({
          name: data.name,
          phoneNumber: data.phoneNumber,
        });
        fetchOptions.headers = {
          "Content-Type": "application/json",
        };
      }
      fetchOptions.body = body;
      const response = await fetch(
        `${window.ENV.BACKEND_URL}/api/v1/profile`,
        fetchOptions,
      );
      if (!response.ok) {
        throw new Error("Failed to update profile");
      }
      await checkLoginStatus();
      toast.success("Profile updated successfully");
    } catch (error) {
      showErrorToast(error);
      console.error("Error updating profile:", error);
    } finally {
      setIsSubmitting(false);
    }
  };
  if (initialLoading || isLoading) {
    return (
      <div className="py-5 px-4 md:px-6 space-y-6">
        <Skeleton className="h-8 w-1/3" />
        <div className="flex items-center space-x-4">
          <Skeleton className="w-24 h-24 rounded-full" />
          <div className="space-y-2">
            <Skeleton className="h-6 w-32" />
            <Skeleton className="h-10 w-28" />
          </div>
        </div>
        <div className="space-y-4">
          <Skeleton className="h-10 w-full" />
          <Skeleton className="h-10 w-full" />
          <Skeleton className="h-10 w-24" />
        </div>
      </div>
    );
  }
  if (!user) {
    return <div>Error: Unable to load user data</div>;
  }
  return (
    <ClientOnly>
      {() => (
        <div className="py-5">
          <div className="px-4 md:px-6 py-3 space-y-6">
            <header className="space-y-1.5">
              <h1 className="text-2xl font-bold">Edit Account</h1>
            </header>
            <div className="flex items-center space-x-4">
              <Avatar className="w-24 h-24 border-2 border-white/[0.1]">
                <AvatarImage
                  src={userAvatar ?? "/placeholder.svg"}
                  alt="User avatar"
                />
              </Avatar>
              <div className="space-y-1.5">
                <h1 className="text-2xl font-bold">{user.profile.name}</h1>
                <Dialog>
                  <DialogTrigger asChild>
                    <Button variant="outline">Change Avatar</Button>
                  </DialogTrigger>
                  <DialogContent className="sm:max-w-[425px] bg-background">
                    <DialogHeader>
                      <DialogTitle>Change avatar</DialogTitle>
                      <DialogDescription>
                        Change your avatar by generating a new one. Once you are
                        done, click on the close button.
                      </DialogDescription>
                    </DialogHeader>
                    <div className="flex flex-col justify-center mx-auto w-full p-2 items-center gap-4">
                      <div className="flex flex-row items-center gap-4">
                        <Avatar className="w-24 h-24 border-2 border-white/[0.1]">
                          <AvatarImage
                            src={userAvatar ?? ""}
                            alt="User avatar"
                          />
                        </Avatar>
                      </div>
                      <div className="flex flex-row items-center gap-4">
                        <Button onClick={generateAvatar} variant="outline">
                          Generate New Avatar
                        </Button>
                      </div>
                    </div>
                    <DialogFooter>
                      <DialogClose asChild>
                        <Button type="button" variant="secondary">
                          Close
                        </Button>
                      </DialogClose>
                    </DialogFooter>
                  </DialogContent>
                </Dialog>
              </div>
            </div>
            <Form {...form} key={formKey}>
              <form
                onSubmit={form.handleSubmit(onSubmit)}
                className="space-y-8"
              >
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Name</FormLabel>
                      <FormControl>
                        <Input placeholder="Your name" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="phoneNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Phone Number</FormLabel>
                      <FormControl>
                        <PhoneInput {...field} placeholder="Phone number" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <Button type="submit" disabled={isSubmitting}>
                  {isSubmitting ? "Updating..." : "Update profile"}
                </Button>
              </form>
            </Form>
          </div>
        </div>
      )}
    </ClientOnly>
  );
}

// Define the type for the loader data
type LoaderData = {
  sellerProfile: SellerProfileFormData | null;
};
export const loader: LoaderFunction = async ({ request }) => {
  const response = await fetch(
    `${process.env.BACKEND_URL}/api/v1/seller-profile`,
    {
      headers: {
        Cookie: request.headers.get("Cookie") || "",
      },
    },
  );
  if (!response.ok) {
    throw new Error("Failed to fetch seller profile");
  }
  const sellerProfile = await response.json();
  const loaderData: LoaderData = {
    sellerProfile: sellerProfile
      ? {
          businessName: sellerProfile.businessName,
          businessAddress: sellerProfile.businessAddress,
          businessPhone: sellerProfile.businessPhone,
          businessEmail: sellerProfile.businessEmail,
          accountHolderName: sellerProfile.bankAccount?.accountHolderName || "",
          accountNumber: sellerProfile.bankAccount?.accountNumber || "",
          bankName: sellerProfile.bankAccount?.bankName || "",
          swiftCode: sellerProfile.bankAccount?.swiftCode || "",
          iban: sellerProfile.bankAccount?.iban || "",
          routingNumber: sellerProfile.bankAccount?.routingNumber || "",
        }
      : null,
  };
  return json(loaderData);
};
const SellerProfileEditPage: React.FC = () => {
  const { sellerProfile } = useLoaderData<LoaderData>();
  const handleSubmit = async (data: SellerProfileFormData) => {
    try {
      const response = await fetch(
        `${window.ENV.BACKEND_URL}/api/v1/seller-profile`,
        {
          method: "PUT",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(data),
        },
      );
      if (!response.ok) {
        throw new Error("Failed to update seller profile");
      }
      toast.success("Seller profile updated successfully");
    } catch (error) {
      showErrorToast(error);
      console.error("Error updating seller profile:", error);
    }
  };
  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Edit Your Seller Profile</h1>
      <SellerProfileEditComponent
        initialData={sellerProfile || undefined}
        onSubmit={handleSubmit}
      />
    </div>
  );
};
