    `${process.env.BACKEND_URL}/api/v1/code-analysis/${id}`,
    {
      credentials: "include",
      headers: {
        Cookie: cookieHeader?.toString() || "",
      },
    },
  );
  let codeAnalysis = null;
  if (analysisResponse.ok) {
    codeAnalysis = await analysisResponse.json();
  }
  return json({
    repo: data.repo,
    codeAnalysis: codeAnalysis,
  });
};
