interface DataTableRowActionsProps {
 row: Row;
 tableSchema: ZodSchema;
}

export function PayoutRequestTableRowActions({
 row,
 tableSchema,
}: DataTableRowActionsProps) {
 const payoutRequest = tableSchema.parse(row.original);
 const [activeDialog, setActiveDialog] = useState(null);

 const handleAction = async (action: string) => {
 try {
 const url = `${window.ENV.BACKEND_URL}/api/v1/payout-requests/${payoutRequest.id}/process`;
 const res = await fetch(url, {
 method: "POST",
 credentials: "include",
 headers: {
 "Content-Type": "application/json",
 },
 body: JSON.stringify({ action }),
 });

 if (!res.ok) {
 throw new Error("Oh no! Something went wrong!");
 }

 toast.success(`Payout request ${action}ed successfully`);
 window.location.reload();
 } catch (e) {
 showErrorToast(e);
 }
 };

 const renderDialogContent = () => {
 switch (activeDialog) {
 case "process":
 return (
 setActiveDialog(null)}
 onAction={handleAction}
 />
 );
 default:
 return null;
 }
 };

 return (
 setActiveDialog(null)}
 >




 Open menu



 setActiveDialog("process")}
 disabled={row.getValue("processedAt")}
 >
 Process Request




 {renderDialogContent()}

 );
}

