interface DataTableRowActionsProps {
 row: Row;
 tableSchema: z.ZodSchema;
}

export function UserTableRowActions({
 row,
 tableSchema,
}: DataTableRowActionsProps) {
 const user = tableSchema.parse(row.original);
 const [activeDialog, setActiveDialog] = React.useState(null);

 const handleAction = async (action: string, status?: string) => {
 try {
 let url = `${window.ENV.BACKEND_URL}/api/v1/admin/${user.email}`;
 let method = "POST";
 let body = null;

 switch (action) {
 case "ban":
 url += "/ban";
 break;
 case "unban":
 url += "/unban";
 break;
 case "delete":
 method = "DELETE";
 break;
 case "reviewApplication":
 url = `${window.ENV.BACKEND_URL}/api/v1/admin/seller-profile/${user.email}`;
 method = "PUT";
 body = JSON.stringify({ verificationStatus: status });
 break;
 default:
 throw new Error("Invalid action");
 }

 const res = await fetch(url, {
 method: method,
 credentials: "include",
 headers: {
 "Content-Type": "application/json",
 },
 body: body,
 });

 if (!res.ok) {
 throw new Error("Oh no! Something went wrong!");
 }

 toast.success(`Action ${action} completed successfully`);
 window.location.reload();
 } catch (e) {
 showErrorToast(e);
 }
 };

 const renderDialogContent = () => {
 switch (activeDialog) {
 case "ban":
 return (
 setActiveDialog(null)}
 onAction={() => handleAction("ban")}
 />
 );
 case "unban":
 return (
 setActiveDialog(null)}
 onAction={() => handleAction("unban")}
 />
 );
 case "delete":
 return (
 setActiveDialog(null)}
 onAction={() => handleAction("delete")}
 />
 );
 case "reviewApplication":
 return (
 setActiveDialog(null)}
 onAction={(action, status) => handleAction(action, status)}
 />
 );
 default:
 return null;
 }
 };

 return (
 setActiveDialog(null)}
 >




 Open menu



 {!user.bannedUntil ? (

 Edit Profile

 ) : (
 Edit Profile
 )}
 {user.role === "SELLER" && (
 <>

 Edit Seller Profile

 setActiveDialog("reviewApplication")}
 >
 Review Application

 </>
 )}
 {user.bannedUntil ? (
 setActiveDialog("unban")}>
 Unban User

 ) : (
 setActiveDialog("ban")}
 className="text-red-500"
 >
 Ban User

 )}
 setActiveDialog("delete")}
 className="text-red-500"
 >
 Delete User





 {renderDialogContent()}


 );
}

