interface FlagDistributionChartProps {
 flagCounts: Record;
}

interface FlagCount {
 name: string;
 value: number;
 key: string;
}

const FLAG_COLORS = {
 FALSE_INFORMATION: "#FF6384",
 OTHER: "#36A2EB",
 HARASSMENT: "#FFCE56",
 OFF_TOPIC: "#4BC0C0",
 INAPPROPRIATE_LANGUAGE: "#9966FF",
 SPAM: "#FF9F40",
};

const FlagDistributionChart: React.FC = ({
 flagCounts,
}) => {
 const data: FlagCount[] = Object.entries(flagCounts)
 .map(([key, value]) => ({
 name: flagOptions.find((option) => option.value === key)?.label || key,
 value: value,
 key: key,
 }))
 .sort((a, b) => b.value - a.value);

 const chartConfig: ChartConfig = data.reduce((config, item) => {
 config[item.key] = {
 label: item.name,
 color: FLAG_COLORS[item.key as keyof typeof FLAG_COLORS] || "#000000",
 };
 return config;
 }, {} as ChartConfig);

 const renderCustomizedLabel = ({
 cx,
 cy,
 midAngle,
 innerRadius,
 outerRadius,
 percent,
 }: any) => {
 const RADIAN = Math.PI / 180;
 const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
 const x = cx + radius * Math.cos(-midAngle * RADIAN);
 const y = cy + radius * Math.sin(-midAngle * RADIAN);

 return (
 cx ? "start" : "end"}
 dominantBaseline="central"
 >
 {`${(percent * 100).toFixed(0)}%`}

 );
 };

 return (


 Flag Distribution






 {data.map((entry, index) => (

 ))}

 {
 const total = data.reduce((sum, item) => sum + item.value, 0);
 const percent = ((value / total) * 100).toFixed(1);
 return [`${value} (${percent}%)`, name];
 }}
 />






 );
