 tableSchema: ZodSchema;
}

export function CommentTableRowActions({
 row,
 tableSchema,
}: DataTableRowActionsProps) {
 const comment = tableSchema.parse(row.original);
 const [activeDialog, setActiveDialog] = React.useState(null);

 const handleAction = async (action: string) => {
 try {
 // eslint-disable-next-line
 let url = `${window.ENV.BACKEND_URL}/api/v1/comments/${comment.id}`;
 let method = "PUT";

 switch (action) {
 case "removeFlag":
 url += "/revert";
 method = "PUT";
 break;
 case "delete":
 method = "DELETE";
 break;
 default:
 throw new Error("Invalid action");
 }

 const res = await fetch(url, {
 method: method,
 credentials: "include",
 headers: {
 "Content-Type": "application/json",
 },
 body: JSON.stringify(action === "removeFlag" ? { flag: "NONE" } : {}),
 });

 if (!res.ok) {
 throw new Error("Oh no! Something went wrong!");
 }

 toast.success(
 `Comment ${action === "removeFlag" ? "updated" : "deleted"} successfully`,
 );
 window.location.reload();
 } catch (e) {
 showErrorToast(e);
 }
 };

 const renderDialogContent = () => {
 switch (activeDialog) {
 case "removeFlag":
 return (
 setActiveDialog(null)}
 onAction={() => handleAction("removeFlag")}
 />
 );
 case "delete":
 return (
 setActiveDialog(null)}
 onAction={() => handleAction("delete")}
 />
 );
 default:
 return null;
 }
 };

 return (
 setActiveDialog(null)}
 >




 Open menu



 setActiveDialog("removeFlag")}>
 Remove Flag

 setActiveDialog("delete")}
 className="text-red-500"
 >
 Delete Comment




 {renderDialogContent()}

 );
}

