 const monaco = monacoRef.current;
 const currentValue = editorRef.current.getValue();
 const newModel = monaco.editor.createModel(
 currentValue,
 editorOptions.language,
 monaco.Uri.file(
 editorOptions.language === "javascript" ? "index.jsx" : "index.tsx",
