import { create } from "zustand";
import { devtools, persist } from "zustand/middleware";
import { StripeElementsOptions } from "@stripe/stripe-js";

interface CheckoutState {
 clientSecret: string | null;
 isLoading: boolean;
 error: string | null;
 repoId: string | null;
 appearance: StripeElementsOptions["appearance"];
}

interface CheckoutActions {
 setClientSecret: (secret: string) => void;
 setIsLoading: (isLoading: boolean) => void;
 setError: (error: string | null) => void;
 setRepoId: (repoId: string) => void;
 resetCheckout: () => void;
 initializeCheckout: (repoId: string) => Promise;
 handlePurchase: (repoId: string) => Promise;
}

type CheckoutStore = CheckoutState & CheckoutActions;

const DEFAULT_APPEARANCE: StripeElementsOptions["appearance"] = {
 theme: "stripe",
 variables: {
 colorPrimary: "#0F172A",
 colorBackground: "#ffffff",
 colorText: "#1e293b",
 colorDanger: "#ef4444",
 fontFamily:
 'ui-sans-serif, system-ui, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji"',
 spacingUnit: "4px",
 borderRadius: "8px",
 },
 rules: {
 ".Input": {
 border: "1px solid #e2e8f0",
 boxShadow: "0 1px 2px 0 rgb(0 0 0 / 0.05)",
 },
 ".Input:focus": {
 border: "1px solid #0F172A",
 boxShadow: "0 0 0 1px #0F172A",
 },
 ".Label": {
 fontWeight: "500",
 },
 ".Error": {
 color: "#ef4444",
 },
 },
};

export const useCheckoutStore = create()(
 devtools(
 persist(
 (set, get) => ({
 clientSecret: null,
 isLoading: false,
 error: null,
 repoId: null,
 appearance: DEFAULT_APPEARANCE,
 setClientSecret: (secret) => set({ clientSecret: secret }),
 setIsLoading: (isLoading) => set({ isLoading }),
 setError: (error) => set({ error }),
 setRepoId: (repoId) => set({ repoId }),
 resetCheckout: () =>
 set({
 clientSecret: null,
 isLoading: false,
 error: null,
 repoId: null,
 }),
 initializeCheckout: async (repoId) => {
 const { setIsLoading, setClientSecret, setError, setRepoId } = get();
 setIsLoading(true);
 setRepoId(repoId);
 try {
 const response = await fetch(
 `${window.ENV.BACKEND_URL}/api/v1/checkout`,
 {
 method: "POST",
 headers: { "Content-Type": "application/json" },
 body: JSON.stringify({ repoId }),
 credentials: "include",
 },
 );
 if (!response.ok) {
 throw new Error("Failed to create payment intent");
 }
 const { clientSecret } = await response.json();
 setClientSecret(clientSecret);
 setError(null);
 } catch (error) {
 setError(
 error instanceof Error
 ? error.message
 : "An unexpected error occurred",
 );
 } finally {
 setIsLoading(false);
 }
 },
 handlePurchase: async (repoId) => {
 const { setIsLoading, setClientSecret, setError, setRepoId } = get();
 setIsLoading(true);
 setRepoId(repoId);
 try {
 const response = await fetch(
 `${window.ENV.BACKEND_URL}/api/v1/checkout`,
 {
 method: "POST",
 headers: { "Content-Type": "application/json" },
 body: JSON.stringify({ repoId }),
 credentials: "include",
 },
 );
 if (!response.ok) {
 throw new Error("Failed to create checkout");
 }
 const { clientSecret } = await response.json();
 setClientSecret(clientSecret);
 setError(null);
 return true;
 } catch (error) {
 setError(
 error instanceof Error
 ? error.message
 : "An unexpected error occurred",
 );
 return false;
 } finally {
 setIsLoading(false);
 }
 },
 }),
 {
 name: "checkout-store",
 partialize: (state) => ({
 repoId: state.repoId,
 clientSecret: state.clientSecret,
 appearance: state.appearance,
 }),
 },
 ),
 ),
);

import { create, StateCreator } from "zustand";
import { persist, PersistOptions } from "zustand/middleware";
import {
 DEFAULT_REACT_MONACO,
 DEFAULT_CSS_MONACO,
} from "@/integrations/monaco/constants";

export type EditorLanguage = "javascript" | "typescript" | "css";
export type EditorTheme = "vs-dark" | "vs-light" | "hc-black";
export type WordWrapSetting = "on" | "off" | "wordWrapColumn" | "bounded";

interface EditorOptions {
 theme: EditorTheme;
 fontSize: number;
 language: EditorLanguage;
 wordWrap: WordWrapSetting;
 minimap: { enabled: boolean };
 lineNumbers: "on" | "off" | "relative";
}

export interface MonacoStoreType {
 editorValue: string;
 cssValue: string;
 editorOptions: EditorOptions;
 handleEditorChange: (value: string, language: EditorLanguage) => void;
 setEditorOptions: (options: Partial) => void;
 resetEditorContent: (language: EditorLanguage) => void;
 toggleMinimap: () => void;
 increaseFontSize: () => void;
 decreaseFontSize: () => void;
 setEditorValue: (value: string) => void;
 setCssValue: (value: string) => void;
}

const DEFAULT_EDITOR_OPTIONS: EditorOptions = {
 theme: "vs-dark",
 fontSize: 14,
 language: "typescript",
 wordWrap: "on",
 minimap: { enabled: true },
 lineNumbers: "on",
};

type PersistedState = {
 editorOptions: EditorOptions;
};

type MonacoStoreCreator = StateCreator<
 MonacoStoreType,
 [],
 [["zustand/persist", PersistedState]]
>;

const createMonacoStore: MonacoStoreCreator = (set) => ({
 editorValue: "",
 cssValue: "",
 editorOptions: DEFAULT_EDITOR_OPTIONS,
 handleEditorChange: (value: string, language: EditorLanguage) =>
 set(() => {
 if (language === "css") {
 return { cssValue: value };
 }
 return { editorValue: value };
 }),
 setEditorOptions: (options: Partial) =>
 set((state) => ({
 editorOptions: { ...state.editorOptions, ...options },
 })),
 resetEditorContent: (language: EditorLanguage) =>
 set(() => {
 if (language === "css") {
 return { cssValue: DEFAULT_CSS_MONACO };
 }
 return { editorValue: DEFAULT_REACT_MONACO };
 }),
 toggleMinimap: () =>
 set((state) => ({
 editorOptions: {
 ...state.editorOptions,
 minimap: { enabled: !state.editorOptions.minimap.enabled },
 },
 })),
 increaseFontSize: () =>
 set((state) => ({
 editorOptions: {
 ...state.editorOptions,
 fontSize: state.editorOptions.fontSize + 1,
 },
 })),
 decreaseFontSize: () =>
 set((state) => ({
 editorOptions: {
 ...state.editorOptions,
 fontSize: Math.max(8, state.editorOptions.fontSize - 1),
 },
 })),
 setEditorValue: (value: string) => set({ editorValue: value }),
 setCssValue: (value: string) => set({ cssValue: value }),
});

const persistOptions: PersistOptions = {
 name: "monaco-store",
 partialize: (state) => ({
 editorOptions: state.editorOptions,
 }),
};

export const useMonacoStore = create()(
 persist(createMonacoStore, persistOptions),
);

import { create } from "zustand";
import { devtools } from "zustand/middleware";

interface User {
 id: string;
 name?: string;
 avatar?: string;
}

interface Review extends BackendReview {
 user?: User;
 commentCount?: number;
}

interface Comment extends BackendComment {
 user?: User;
}

interface Pagination {
 total: number;
 page: number;
 perPage: number;
 lastPage: number;
}

interface ReviewsPagination {
 data: Review[];
 meta: Pagination;
}

interface CommentsPagination {
 data: Comment[];
 meta: Pagination;
}

const getApiBaseUrl = () => {
 if (typeof window !== "undefined") {
 return `${window?.ENV?.BACKEND_URL}/api/v1`;
 }
 // Provide a fallback or default URL for server-side rendering
 return `${process.env.BACKEND_URL}/api/v1`;
};

interface ReviewStore {
 reviews: ReviewsPagination;
 expandedReviews: string[];
 comments: { [key: string]: CommentsPagination };
 loadingReviews: boolean;
 loadingComments: { [key: string]: boolean };
 API_BASE_URL: string;

 setReviews: (reviews: ReviewsPagination) => void;
 toggleExpand: (reviewId: string) => void;
 setComments: (reviewId: string, comments: CommentsPagination) => void;
 setLoadingReviews: (loading: boolean) => void;
 setLoadingComments: (reviewId: string, loading: boolean) => void;

 fetchReviews: (repoId: string, page?: number) => Promise;
 fetchComments: (
 repoId: string,
 reviewId: string,
 page?: number,
 ) => Promise;
 loadMoreReviews: (repoId: string) => Promise;
 loadMoreComments: (repoId: string, reviewId: string) => Promise;
 handleVote: (
 reviewId: string,
 voteType: "upvote" | "downvote",
 ) => Promise;
 handleCommentVote: (
 repoId: string,
 commentId: string,
 voteType: "upvote" | "downvote",
 ) => Promise;
 addReview: (
 repoId: string,
 newReviewData: Omit<
 BackendReview,
 | "id"
 | "userId"
 | "repoId"
 | "createdAt"
 | "updatedAt"
 | "deletedAt"
 | "flag"
 | "upvotes"
 | "downvotes"
 >,
 ) => Promise;
 addComment: (
 repoId: string,
 reviewId: string,
 commentContent: string,
 ) => Promise;
}

const useReviewStore = create()(
 devtools((set, get) => ({
 reviews: {
 data: [],
 meta: { total: 0, page: 1, perPage: 10, lastPage: 1 },
 },
 expandedReviews: [],
 comments: {},
 loadingReviews: false,
 loadingComments: {},
 API_BASE_URL: getApiBaseUrl(),

 setReviews: (reviews) => set({ reviews }),
 toggleExpand: (reviewId) =>
 set((state) => ({
 expandedReviews: state.expandedReviews.includes(reviewId)
 ? state.expandedReviews.filter((id) => id !== reviewId)
 : [...state.expandedReviews, reviewId],
 })),
 setComments: (reviewId, comments) =>
 set((state) => ({
 comments: {
 ...state.comments,
 [reviewId]: {
 ...comments,
 data:
 comments.meta.page === 1
 ? comments.data
 : [...(state.comments[reviewId]?.data || []), ...comments.data],
 },
 },
 })),
 setLoadingReviews: (loading) => set({ loadingReviews: loading }),
 setLoadingComments: (reviewId, loading) =>
 set((state) => ({
 loadingComments: { ...state.loadingComments, [reviewId]: loading },
 })),

 fetchReviews: async (repoId, page = 1) => {
 get().setLoadingReviews(true);
 try {
 const response = await fetch(
 `${get().API_BASE_URL}/repo/${repoId}/reviews?page=${page}&perPage=10`,
 { credentials: "include" },
 );
 if (!response.ok) throw new Error("Failed to fetch reviews");
 const data: ReviewsPagination = await response.json();
 set((state) => ({
 reviews:
 page === 1
 ? data
 : {
 ...data,
 data: [...state.reviews.data, ...data.data],
 },
 }));
 } catch (error) {
 console.error("Error fetching reviews:", error);
 } finally {
 get().setLoadingReviews(false);
 }
 },

 fetchComments: async (repoId, reviewId, page = 1) => {
 get().setLoadingComments(reviewId, true);
 try {
 const response = await fetch(
 `${get().API_BASE_URL}/repo/${repoId}/reviews/${reviewId}?page=${page}&perPage=10`,
 { credentials: "include" },
 );
 if (!response.ok) throw new Error("Failed to fetch comments");
 const data: CommentsPagination = await response.json();
 get().setComments(reviewId, data);
 } catch (error) {
 console.error("Error fetching comments:", error);
 } finally {
 get().setLoadingComments(reviewId, false);
 }
 },

 loadMoreReviews: async (repoId) => {
 const currentPage = get().reviews.meta.page;
 const nextPage = currentPage + 1;
 if (nextPage <= get().reviews.meta.lastPage) {
 await get().fetchReviews(repoId, nextPage);
 }
 },

 loadMoreComments: async (repoId, reviewId) => {
 const currentPage = get().comments[reviewId]?.meta.page || 0;
 const nextPage = currentPage + 1;
 await get().fetchComments(repoId, reviewId, nextPage);
 },

 handleVote: async (reviewId, voteType) => {
 try {
 const response = await fetch(
 `${get().API_BASE_URL}/reviews/${reviewId}/${voteType}`,
 { method: "POST", credentials: "include" },
 );
 if (!response.ok) throw new Error(`Failed to ${voteType} review`);
 // Refresh reviews after voting
 await get().fetchReviews(get().reviews.data[0].repoId);
 } catch (error) {
 console.error(`Error ${voteType}ing review:`, error);
 }
 },

 handleCommentVote: async (repoId, commentId, voteType) => {
 try {
 const response = await fetch(
 `${get().API_BASE_URL}/comments/${commentId}/${voteType}`,
 { method: "POST", credentials: "include" },
 );
 if (!response.ok) throw new Error(`Failed to ${voteType} comment`);
 // Find the review that contains this comment and refresh its comments
 const reviewId = Object.keys(get().comments).find((reviewId) =>
 get().comments[reviewId].data.some(
 (comment) => comment.id === commentId,
 ),
 );
 if (reviewId) await get().fetchComments(repoId, reviewId);
 } catch (error) {
 console.error(`Error ${voteType}ing comment:`, error);
 }
 },

 addReview: async (repoId, newReviewData) => {
 try {
 const response = await fetch(`${get().API_BASE_URL}/reviews`, {
 method: "POST",
 headers: { "Content-Type": "application/json" },
 body: JSON.stringify({ ...newReviewData, repoId }),
 credentials: "include",
 });
 if (!response.ok) throw new Error("Failed to add review");
 await get().fetchReviews(repoId);
 } catch (error) {
 console.error("Error adding review:", error);
 }
 },

 addComment: async (repoId, reviewId, commentContent) => {
 try {
 const response = await fetch(`${get().API_BASE_URL}/comments`, {
 method: "POST",
 headers: { "Content-Type": "application/json" },
 body: JSON.stringify({ content: commentContent, reviewId }),
 credentials: "include",
 });
 if (!response.ok) throw new Error("Failed to add comment");
 // Fetch only the first page of comments after adding a new one
 await get().fetchComments(repoId, reviewId, 1);
 } catch (error) {
 console.error("Error adding comment:", error);
 }
 },
 })),
);

export default useReviewStore;

