const MAX_TAGS = 5;

export const SearchFilterSchema = z.object({
 searchQuery: z.string().optional(),
 tags: z
 .array(z.string())
 .max(MAX_TAGS, `You can only add up to ${MAX_TAGS} tags`),
 language: z.string().optional(),
});

export type SearchFilterSchemaType = z.infer;

interface SearchResult {
 id: string;
 name: string;
 description: string;
 tags: string[];
 language: string;
 visibility: string;
}

interface SearchAndFilterProps {
 onSearch: (results: SearchResult[]) => void;
}

const LANGUAGES = [
 { value: "javascript", label: "JavaScript" },
 { value: "typescript", label: "TypeScript" },
 { value: "python", label: "Python" },
 { value: "java", label: "Java" },
 { value: "csharp", label: "C#" },
];

export default function SearchAndFilter({ onSearch }: SearchAndFilterProps) {
 const [isLoading, setIsLoading] = useState(false);
 const [searchResults, setSearchResults] = useState([]);

 const form = useForm({
 resolver: zodResolver(SearchFilterSchema),
 defaultValues: {
 searchQuery: "",
 tags: [],
 language: "",
 },
 });

 const {
 control,
 handleSubmit,
 formState: { errors },
 setValue,
 watch,
 reset,
 } = form;
 const watchedTags = watch("tags");

 const addTag = useCallback(
 (tag: string) => {
 if (
 tag.trim() &&
 !watchedTags.includes(tag.trim()) &&
 watchedTags.length < MAX_TAGS
 ) {
 setValue("tags", [...watchedTags, tag.trim()]);
 }
 },
 [watchedTags, setValue],
 );

 const removeTag = useCallback(
 (tagToRemove: string) => {
 setValue(
 "tags",
 watchedTags.filter((tag) => tag !== tagToRemove),
 );
 },
 [watchedTags, setValue],
 );

 const handleSearch = async (data: SearchFilterSchemaType) => {
 setIsLoading(true);
 try {
 const queryParams = new URLSearchParams({
 ...(data.searchQuery && { q: data.searchQuery }),
 ...(data.tags.length && { tags: data.tags.join(",") }),
 ...(data.language && { language: data.language }),
 }).toString();

 const response = await fetch(
 `${window.ENV.BACKEND_URL}/api/v1/repos/search?${queryParams}`,
 { method: "GET" },
 );

 if (!response.ok) {
 throw new Error("Failed to fetch search results");
 }

 const results: SearchResult[] = await response.json();
 setSearchResults(results);
 onSearch(results);
 } catch (error) {
 showErrorToast(error);
 setSearchResults([]);
 } finally {
 setIsLoading(false);
 }
 };

 const clearForm = () => {
 reset();
 setSearchResults([]);
 };

 return (


 (

 Search





 )}
 />

 (

 Tags



 {
 if (e.key === "Enter" || e.key === ",") {
 e.preventDefault();
 addTag((e.target as HTMLInputElement).value);
 (e.target as HTMLInputElement).value = "";
 }
 }}
 />


 {field.value.map((tag, index) => (

 {tag}
 removeTag(tag)}
 />

 ))}



 {errors.tags?.message}

 )}
 />

 (

 Language




 {field.value
 ? LANGUAGES.find(
 (language) => language.value === field.value,
 )?.label
 : "Select language"}







 No language found.

 {LANGUAGES.map((language) => (
 {
 setValue("language", language.value);
 }}
 >

 {language.label}

 ))}








 )}
 />




 {isLoading ? "Searching..." : "Search"}


 Clear




 {searchResults.length > 0 && (



Search Results
 {searchResults.map((result) => (



{result.name}

{result.description}


 {result.tags.map((tag, index) => (

 {tag}

 ))}


Language: {result.language}

Visibility: {result.visibility}

 ))}

 )}

 );
}

import { showErrorToast } from "@/lib/handle-error";
import { useNavigate } from "@remix-run/react";
import {
 useStripe,
 useElements,
 PaymentElement,
} from "@stripe/react-stripe-js";
import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { toast } from "sonner";

const CheckoutForm = () => {
 const navigate = useNavigate();
 const stripe = useStripe();
 const elements = useElements();
 const [isProcessing, setIsProcessing] = useState(false);
 const [requiresRecovery, setRequiresRecovery] = useState(false);
 const [recoveryPaymentIntentId, setRecoveryPaymentIntentId] = useState<
 string | null
 >(null);

 useEffect(() => {
 if (stripe && elements) {
 console.log("Stripe and elements are ready");
 }
 }, [stripe, elements]);

 const processPaymentOnServer = async (
 paymentIntentId: string,
 ): Promise<{ success: boolean; orderId?: string }> => {
 try {
 if (!window.ENV || !window.ENV.BACKEND_URL) {
 throw new Error("Backend URL is not configured");
 }

 const response = await fetch(
 `${window.ENV.BACKEND_URL}/api/v1/checkout/process-payment`,
 {
 method: "POST",
 headers: {
 "Content-Type": "application/json",
 },
 body: JSON.stringify({ paymentIntentId }),
 credentials: "include",
 },
 );

 if (!response.ok) {
 const errorData = await response.json();
 throw new Error(
 errorData.message || "Failed to process payment on server",
 );
 }

 return await response.json();
 } catch (error) {
 console.error("Error processing payment on server:", error);
 throw error;
 }
 };

 const handleSubmit = async (event: React.FormEvent) => {
 event.preventDefault();

 if (!stripe || !elements) {
 toast.error("Stripe is not loaded yet.");
 return;
 }

 setIsProcessing(true);

 try {
 const result = await stripe.confirmPayment({
 elements,
 confirmParams: {},
 redirect: "if_required",
 });

 console.log("Payment confirmation result:", result);

 if (result.error) {
 throw new Error(result.error.message);
 }

 if (result.paymentIntent && result.paymentIntent.status === "succeeded") {
 try {
 const processResult = await processPaymentOnServer(
 result.paymentIntent.id,
 );

 if (processResult.success) {
 toast.success("Payment Successful", {
 description: "Your payment has been processed successfully.",
 });
 navigate("/checkout/success", {
 replace: true,
 state: { orderId: processResult.orderId },
 });
 } else {
 throw new Error("Failed to process payment on the server.");
 }
 } catch (serverError) {
 console.error("Server processing failed:", serverError);
 setRequiresRecovery(true);
 setRecoveryPaymentIntentId(result.paymentIntent.id);
 toast.error(
 "Payment recorded but order processing failed. Please try recovery or contact support.",
 {
 duration: 10000,
 },
 );
 }
 } else {
 throw new Error("Payment was not successful.");
 }
 } catch (error) {
 showErrorToast(error);
 console.error("Payment error:", error);
 } finally {
 setIsProcessing(false);
 }
 };

 const handleRecovery = async () => {
 if (!recoveryPaymentIntentId) {
 toast.error("No payment to recover.");
 return;
 }

 setIsProcessing(true);
 try {
 const processResult = await processPaymentOnServer(
 recoveryPaymentIntentId,
 );

 if (processResult.success) {
 toast.success("Recovery Successful", {
 description: "Your order has been successfully processed.",
 });
 navigate("/checkout/success", {
 replace: true,
 state: { orderId: processResult.orderId },
 });
 } else {
 throw new Error("Failed to recover the order.");
 }
 } catch (error) {
 showErrorToast(error);
 console.error("Recovery error:", error);
 toast.error("Recovery failed. Please contact customer support.", {
 duration: 10000,
 });
 } finally {
 setIsProcessing(false);
 }
 };

 return (


 {!requiresRecovery ? (

 {isProcessing ? "Processing..." : "Pay Now"}

 ) : (




 Payment recorded but order processing failed. Please try recovery or
 contact support.


 {isProcessing ? "Recovering..." : "Attempt Recovery"}


 )}

 );
};

export default CheckoutForm;
